import {useState, useEffect} from 'react';
import './anime.js'

function Card (props) {
  const [mouseState, setMouseState] = useState(false);  
    if (mouseState ===false) {
      return(<AnimeCard onMouseOver = {() => {setMouseState(true)}} thisAnime = {props.thisAnime}/>)
    }
    else {
      return(<AnimeInfo onMouseLeave={()=>{setMouseState(false)}} thisAnime = {props.thisAnime}/>)
  }
}

function AnimeInfo(props) {
  return(
    <div className=' overflow-scroll shadow-lg mx-2.5 my-2.5 rounded h-40 w-auto flex cursor-pointer'>
      <p>
        {props.thisAnime.synopsis}
      </p>
    </div>
  ) 
}

function AnimeCard(props) {
<div  className = "shadow-lg mx-2.5 my-2.5 rounded h-40 w-auto flex cursor-pointer"> 
    <img className = "shadow-lg rounded" src={props.thisAnime.images.jpg.image_url}/>
    <div className="flex flex-col mx-auto my-auto px-2.5">
      <h1 className="text-center text-sm ">{props.thisAnime.title}</h1>
      <p className = "text-center">{props.thisAnime.score}/10</p>
    </div>
    </div>
}


function TopCharts() {
    const [userData, setUserData] = useState("Loading...");
    const [pageNumber, setPageNumber] = useState(1)
    async function fetchData(){
      const data = await fetch(`https://api.jikan.moe/v4/top/anime?page=${pageNumber}`, {mode: 'cors'});
      setUserData(await data.json());
    }
    useEffect(() => {fetchData()}, []);

    async function nextPage() {
      setUserData("Loading...")
      setPageNumber(pageNumber+1)
      fetchData()
    }

    async function lastPage() {
      setUserData("Loading...")
      setPageNumber(pageNumber-1)
      fetchData()
    }

    if (userData ==="Loading...") {
      return <div className="w-24 h-24 border-t-8 border-blue-700 rounded-full animate-spin absolute inset-2/4"> </div>
    }
    else {
      return (<div className = "grid grid-cols-5 my-2.5"> {userData.data.map((anime)=>{return (<Card thisAnime = {anime} key={anime.mal_id}/>)})} <div className = "w-screen gap-10 flex justify-center"><div className = "text-4xl cursor-pointer"  onClick={lastPage}>←</div> <div className = "text-4xl cursor-pointer" onClick={nextPage}>→</div></div> </div>
      )
    }
}

