import {useState, useEffect} from 'react';
import './anime.js'

function AnimeCard (thisAnime) {
  <div key={thisAnime.mal_id} className = "shadow-lg mx-2.5 my-2.5 rounded h-40 w-auto flex cursor-pointer"> 
      {/* <Link onClick='' to={`${anime.title}`}> */}
      <img className = "shadow-lg rounded"src={thisAnime.images.jpg.image_url}/>
      <div className="flex flex-col mx-auto my-auto px-2.5">
        <h1 className="text-center text-sm ">{thisAnime.title}</h1>
        <p className = "text-center">{thisAnime.score}/10</p>
        {/* if (mouseState = ) */}
      </div>
      {/* </Link>  */}
      </div>
}


function TopCharts() {
    const [userData, setUserData] = useState("Loading...");
    const [mouseState, setMouseState] = useState();
    async function fetchData(){
      const data = await fetch('https://api.jikan.moe/v4/top/anime', {mode: 'cors'});
      setUserData(await data.json());
    }
    useEffect(() => {fetchData()}, []);

    if (userData ==="Loading...") {
      return <div className="w-24 h-24 border-t-8 border-blue-700 rounded-full animate-spin absolute inset-2/4"> </div>
    }
    else {
      return (<div className = "grid grid-cols-5 my-2.5">{userData.data.map((anime)=>{return (<AnimeCard thisAnime = {anime}/>)})}</div>
      )
    }
}

