import {useState, useEffect} from 'react';
import './anime.js'

function AnimeCard (props) {

  const [mouseState, setMouseState] = useState();
  function showAnimeInfo() {
    if (mouseState ===true) {
      return(<AnimeInfo thisAnime = {props.thisAnime}/>)
    }
    return(null)
  }
  return(
  <div onMouseOver = {() => {setMouseState(true)}} onMouseLeave={()=>{setMouseState(false)}} className = "shadow-lg mx-2.5 my-2.5 rounded h-40 w-auto flex cursor-pointer"> 
      <img className = "shadow-lg rounded"src={props.thisAnime.images.jpg.image_url}/>
      <div className="flex flex-col mx-auto my-auto px-2.5">
        <h1 className="text-center text-sm ">{props.thisAnime.title}</h1>
        <p className = "text-center">{props.thisAnime.score}/10</p>
        {showAnimeInfo()}
      </div>
      </div>
  )
}

function AnimeInfo(props) {
  return(
    <div className='rounded shadow-lg absolute w-60 h-60 overflow-scroll bg-gray-700 text-slate-300 p-2.5'>
      <p>
        {props.thisAnime.synopsis}
      </p>
    </div>
  ) 
}


function TopCharts() {
    const [userData, setUserData] = useState("Loading...");
    const [pageNumber, setPageNumber] = useState(1)
    async function fetchData(){
      const data = await fetch(`https://api.jikan.moe/v4/top/anime?page=${pageNumber}`, {mode: 'cors'});
      setUserData(await data.json());
    }
    useEffect(() => {fetchData()}, []);

    async function nextPage() {
      setPageNumber(pageNumber+1)
      fetchData()
    }

    if (userData ==="Loading...") {
      return <div className="w-24 h-24 border-t-8 border-blue-700 rounded-full animate-spin absolute inset-2/4"> </div>
    }
    else {
      return (<div className = "grid grid-cols-5 my-2.5"> {userData.data.map((anime)=>{return (<AnimeCard thisAnime = {anime} key={anime.mal_id}/>)})} <div className = "w-screen gap-10 flex justify-center"><div className = "text-4xl cursor-pointer"  onClick={setPageNumber(pageNumber-1)}>←</div> <div className = "text-4xl cursor-pointer" onClick={fetchData()}>→</div></div> </div>
      )
    }
}

