import {useState, useEffect} from 'react';

function Home() {
    const [userData, setUserData] = useState("Loading...");
    async function fetchData(){
      const data = await fetch('https://api.jikan.moe/v4/top/anime', {mode: 'cors'});
      setUserData(await data.json());
    }
    useEffect(() => {fetchData()}, []);

    if (userData ==="Loading...") {
      return <div>{userData}</div>
    }
    else {
      return (<div>>{userData.data.map((anime)=>{return (<img key={anime.mal_id} src={anime.images.jpg.image_url}/>)})}</div>)
    }
  }


export default Home
