import {useState, useEffect} from 'react';

function TopCharts() {
    const [userData, setUserData] = useState("Loading...");
    async function fetchData(){
      const data = await fetch('https://api.jikan.moe/v4/top/anime', {mode: 'cors'});
      setUserData(await data.json());
    }
    useEffect(() => {fetchData()}, []);

    if (userData ==="Loading...") {
      return <div className="w-24 h-24              border-t-8 border-indigo-500 rounded-full border m-auto animate-spin"> </div>
    }
    else {
      return (<div style={{display: "grid", margin: "10px", gridTemplateColumns:"1fr 1fr 1fr"}}>{userData.data.map((anime)=>{return (<div key={anime.mal_id} style={{border:"5px solid #2E51A2", margin: "10px", borderRadius:"20px"}}> <img style={{display: "block", marginLeft: "auto", marginRight: "auto", marginTop: "10px"}}src={anime.images.jpg.image_url}/> <h1 style={{textAlign: "center"}}>{anime.title}</h1><p style={{textAlign:"center"}}>{anime.synopsis}</p></div>)})}</div>)
    }
}
export default TopCharts
