import {useState, useEffect} from 'react';
import './anime.js'

function TopCharts() {
    const [userData, setUserData] = useState("Loading...");
    async function fetchData(){
      const data = await fetch('https://api.jikan.moe/v4/top/anime', {mode: 'cors'});
      setUserData(await data.json());
    }
    useEffect(() => {fetchData()}, []);

    if (userData ==="Loading...") {
      return <div className="w-24 h-24              border-t-8 border-blue-700 rounded-full m-auto animate-spin"> </div>
    }
    else {
      return (<div className = "grid grid-cols-3">{userData.data.map((anime)=>{return (<div key={anime.mal_id} className = "border-4 border-blue-700 mx-2.5 my-2.5 rounded-md h-fit"> <Link href={anime.url}><img className = "block mx-auto my-2.5" src={anime.images.jpg.image_url}/> <h1 className="text-center text-lg">{anime.title}</h1></Link><p className="text-center text-sm truncate">{anime.synopsis}</p></div>)})}</div>)
    }
}
export default TopCharts
