import {useState, useEffect} from 'react';
import './anime.js'

function TopCharts() {
    const [userData, setUserData] = useState("Loading...");
    async function fetchData(){
      const data = await fetch('https://api.jikan.moe/v4/top/anime', {mode: 'cors'});
      setUserData(await data.json());
    }
    useEffect(() => {fetchData()}, []);

    if (userData ==="Loading...") {
      return <div className="w-24 h-24 border-t-8 border-blue-700 rounded-full animate-spin absolute inset-2/4"> </div>
    }
    else {
      return (<div className = "grid grid-cols-3 my-2.5">{userData.data.map((anime)=>{return (<div key={anime.mal_id} className = "shadow-lg mx-2.5 my-2.5 rounded h-40 w-auto bg-contain bg-no-repeat flex"> 
      {/* <Link onClick='' to={`${anime.title}`}> */}
      <img src={anime.images.jpg.image_url}/>
        <h1 className="text-sm">{anime.title}</h1>
      {/* </Link>  */}
      </div>)})}</div>)
    }
}
export default TopCharts
