import {useState, useEffect} from 'react';

function TopCharts() {
    const [userData, setUserData] = useState("Loading...");
    async function fetchData(){
      const data = await fetch('https://api.jikan.moe/v4/top/anime', {mode: 'cors'});
      setUserData(await data.json());
    }
    useEffect(() => {fetchData()}, []);

    if (userData ==="Loading...") {
      return <div className="loadingBar" style={{width:"300px", height: "10px", border:"2px solid black", borderRadius:"10px", marginLeft: "auto", marginRight:"auto"}}><div style={{animation:"loading 0.5s forward", backgroundColor:"#d98326", height:'10px', width:"0px", borderRadius:"10px"}}></div> <div style={{textAlign:"center", marginLeft: "auto", marginRight:"auto"}}>Loading</div></div>
    }
    else {
      return (<div style={{display: "grid", margin: "10px", gridTemplateColumns:"1fr 1fr 1fr"}}>{userData.data.map((anime)=>{return (<div key={anime.mal_id} style={{border:"5px solid #2E51A2", margin: "10px", borderRadius:"20px"}}> <img style={{display: "block", marginLeft: "auto", marginRight: "auto", marginTop: "10px"}}src={anime.images.jpg.image_url}/> <p>{anime.synopsis}</p></div>)})}</div>)
    }
}
export default TopCharts
