import {useState, useEffect} from 'react';

function Home() {
    const [userData, setUserData] = useState("Loading...");
    async function fetchData(){
      const data = await fetch('https://api.jikan.moe/v4/top/anime', {mode: 'cors'});
      setUserData(await data.json());
    }
    useEffect(() => {fetchData()}, []);

    if (userData !="Loading") {
      return userData.data((anime)=>anime.images.image_url)
    }
    else {
      return <div>{userData}</div>
    }
  }


export default Home
